mapper.select:
SELECT t2.`id`, t4.room_type, t4.room_nick_name, t4.house_detail_name, t2.`house_code`, t2.`house_detail_code`, t2.`house_budget_code`, t3.`budget_code`, t2.`budget_name`, t3. `max_cost` as `budget_amount`, t2.`budget_count`, t2.`status`, t2.`remark`, t2.`version`, t2.`update_time`, t2.`create_time` FROM sbdb.house_info AS t1 INNER JOIN house_budget_info AS t2 ON t1.house_code = t2.house_code INNER JOIN budget_info AS t3 ON t2.budget_code = t3.budget_code INNER JOIN house_detail_info AS t4 ON t2.house_detail_code = t4.house_detail_code WHERE t1.`status` = 0 AND t2.`status` = 0 AND t3.`status` = 0 AND t4.`status` = 0 AND t2.budget_code is not null AND t2.budget_code!=''
AND t1.mobile_phone=#{mobilePhone}
UNION ALL

SELECT t2.`id`, t4.room_type, t4.room_nick_name, t4.house_detail_name, t2.`house_code`, t2.`house_detail_code`, t2.`house_budget_code`, t3.`budget_code`, t2.`budget_name`, t3. `min_cost` as `budget_amount`, t2.`budget_count`, t2.`status`, t2.`remark`, t2.`version`, t2.`update_time`, t2.`create_time` FROM sbdb.house_info AS t1 INNER JOIN house_budget_info AS t2 ON t1.house_code = t2.house_code INNER JOIN budget_info AS t3 ON t2.budget_code = t3.budget_code INNER JOIN house_detail_info AS t4 ON t2.house_detail_code = t4.house_detail_code WHERE t1.`status` = 0 AND t2.`status` = 0 AND t3.`status` = 0 AND t4.`status` = 0 AND t2.budget_code is not null AND t2.budget_code!=''
AND t1.mobile_phone=#{mobilePhone}
UNION ALL
